css:
.print-format {
    position: relative;
}
#id {
    font-size: 5rem;
    font-weight: 300;
}

#type {
    color: rgb(112,128,154);
    font-size: 2.2rem;
}
td {
    text-align: left;
    font-size: 1.5rem;
}
table {
    width: 100%;
    font-size: 1.5rem;
}

table thead tr th {
    font-weight: 350;
}
.divider {
    width: 100%;
    border-bottom: 1px solid rgb(208, 216, 222);
    margin: 2rem 0;
}
section {
    height: 90%;
}
footer {
    bottom: 0;
    height: 10%;
    position: absolute;
    width: 100%;
    text-align: center;
    margin-left: -0.75in;
}
#company {
    font-size: 2rem;
}
